    const ws = wsRef.current;

    ws.onopen = () => {
      console.log('WebSocket connected');
    };
    ws.onmessage = event => {
      setItems(JSON.parse(event.data));
    };

    return () => {
      ws.close();
    };
  }, []);

  const sendNewItem = (item) => {
    const message = JSON.stringify(item);
    const ws = wsRef.current;
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(message);
    }
  }

