    event.preventDefault();
    const messageCpy = [...message];
    const id = new Date().getTime();
    const newMessageValue = newMessage;
    messageCpy.push({id: id, message: newMessageValue});
    sendNewItem({id: id, name: newMessageValue});
    setMessage(messageCpy);
    setNewMessage("");
