// frontend/src/App.js
import React, { useState, useEffect } from 'react';

function App() {
  const [message, setMessage] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [items, setItems] = useState("");


  const ws = useMemo(() => new WebSocket('ws://localhost:5000'), []);

  useEffect(() => {
    ws.onopen = () => {
      console.log('WebSocket connected');
    };
    ws.onmessage = event => {
      setItems(JSON.parse(event.data));
    };
    return () => {
      ws.close();
    };
  }, [ws]);

  const sendNewItem = (item) => {
    const message = JSON.stringify(item);
    ws.send(message);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const messageCpy = [...message];
    const id = new Date().getTime();
    const newMessageValue = newMessage;
    messageCpy.push({id: id, message: newMessageValue});
    sendNewItem({id: id, name: newMessageValue});
    setMessage(messageCpy);
    setNewMessage("");
  }


  const handleChange = (event) => {
    setNewMessage(event.target.value);
  }

  return (
    <div>
      <h1>THE BEST CHAT APP</h1>
      <ul>
        {message.map((msg) => (
          <li key={msg.id}>
            {msg.message}
          </li>
        ))}
      </ul>
      <p>{items}</p>
      <div className="chat_button">
        <form onSubmit={handleSubmit}>  
          <label>
            Your message: <input value={newMessage}
                                name='input'
                                onChange={handleChange} />
            <button>Send</button>
          </label>
        </form>
      </div>
    </div>
  );
}

export default App;
