// frontend/src/App.js
import React, { useState, useEffect } from 'react';

function App() {
  const [message, setMessage] = useState([]);
  const [newMessage, setNewMessage] = useState("tes");
  const [items, setItems] = useState("");
  const [text, setText] = useState("");

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:5000');
    ws.onopen = () => {
      console.log('WebSocket connected');
    };
    ws.onmessage = event => {
      setItems(JSON.parse(event.data));
    };
    return () => {
      ws.close();
    };
  }, []);

  const sendMessage = () => {
    console.log('Message:', text);
  };

  return (
    <div>
      <h1>THE BEST CHAT APP</h1>
      <p>{items}</p>
      <div class="chat_button">
        <label>
          Your message: <input value={newMessage} name='input' onChange={e => setText(e.target.value)} />
          <button onClick={sendMessage}>
            Send
          </button>
        </label>
      </div>
    </div>
  );
}

export default App;
